import React from 'react'
import "./App.css";
import { useState, useEffect } from "react";
import axios from "axios";
import XMLParser from 'react-xml-parser';
import Lyric from "./components/Lyric"



export default function App() {
  const songsName = ['we%20will%20rock%20you', 'frozen']
  const artistName =['queen', 'madonna']
  const [songChoice, setChoice] = useState(0);


  const randomChoice = () => {
    let random = Math.floor(songsName.length*Math.random())+1;
    setChoice(random)
  }


  return (
    <div>
    {randomChoice}
    <Lyric songName = {songsName[songChoice]} artistName = {artistName[songChoice]}/>    
    </div>
  )
}
