import React from 'react'
import { useState, useEffect } from "react";
import axios from "axios";
import XMLParser from 'react-xml-parser';


const Lyric = () => {
  const songsArr = ['we%20will%20rock%20you', 'frozen', 'Beat%20It','Smells%20Like%20Teen%20Spirit','Billie%20Jean']
  const artistsArr =['queen', 'madonna','Michael%20Jackson','Nirvana','Michael%20Jackson']
  const [randomNum, setChoice] = useState(null);
  const [isLoading, setLoading] = useState(false);
  const [lyrics, setLyrics] = useState(null);

  const randomChoice = () => {
    let random = Math.floor(songsArr.length*Math.random());
    setChoice(random)
    console.log(`this is random choice = ${randomNum}`);
  }

  const getData = async(songName,artistName) => {
    console.log(`this is random choice = ${randomNum}`);

    setLoading(true)
    const { data } = await axios.get(
      `https://intense-mesa-62220.herokuapp.com/http://api.chartlyrics.com/apiv1.asmx/SearchLyricDirect?artist=${artistName}&song=${songName}`
    );
    const xml = new XMLParser().parseFromString(data); 
    const lyric = xml.children[9].value
    console.log(lyric);
    setLyrics(lyric);
    setLoading(false);
  }

  useEffect(async () => {
    setLoading(true);
    randomChoice();
    console.log(`this is random choice = ${randomNum}`);
    console.log(`${songsArr[randomNum]} ${artistsArr[randomNum]}`);
    // getData(songsArr[songChoice], artistsArr[songChoice]);
  }, []);


  return (
    <div>
      {lyrics}

    </div>
  )
}

export default Lyric
