import "./App.css";
import { useState } from "react";

const dataObjects = ["one", "two", "three", "four", "five"];

function App() {
  const [data, setData] = useState(dataObjects);

  const handleStatus = (idx) => {
    const dataCopy = [...data];
    dataCopy[idx].completed = !data[idx].completed;
    setData(dataCopy);
  };

  return (
    <div className="App">
      {data.map((item, idx) => {
        const style = {
          textDecoration: item.completed ? "line-through" : "none",
        };
        return (
          <div key={idx}>
            <input type="checkbox" style={style}>
              {item}
            </input>
          </div>
        );
      })}
    </div>
  );
}

export default App;
