import React, { useState } from "react";

const Checkbox = ({name}) => {
  const [checked, setCheck]  = useState(false)

  const changeCheckState = () => {
    setCheck(!checked)
  }

  return (
    <div>
    <input type="checkbox" checked = {checked} onClick = {changeCheckState}/>
    <label>{name}</label>
    </div>
  );
};

export default Checkbox;
