import React from 'react'
import { Link } from "react-router-dom";
import '../index.css';

const Rules = () => {

  return (
    <div className="rules">
    <h1>guess the song title from the lyrics</h1>
    <h1>Rules:</h1>
      <ul>
      <ol>The lyrics will show for 30 seconds. You'll need to submit the song name in the input by that time.</ol>
      <ol>If you'll succeed you'll get 1 point.</ol>
      </ul>
      <div className="link"><Link to={`/game`} className="link">click here to play ></Link></div>
    </div>
  )
}

export default Rules
