import React, {Component} from 'react';
import axios from 'axios'
import ReactDOM from 'react-dom';
// import Player from './Player'

class App extends Component {
  state = {
    joke: null,
    categories: []
  };

  getJoke = async() => {
    const {data} = await axios.get('https://api.chucknorris.io/jokes/random');
    this.setState({joke: data.value});
  }
  getJokeCategories = async() => {
    const {data} = await axios.get('https://api.chucknorris.io/jokes/categories');
    console.log(data);
    this.setState({categories: data});
    this.categories.map(category => {console.log(category);})
  }



  render() {
    return (
      <div>
      <button onClick={this.getJoke}>Click to Get a Joke</button>
      <div>{this.state.joke}</div>
      <button onClick={this.getJokeCategories}>Click to Get Jokes Categories</button>

      </div>
    )
  }
}

export default App;
