import React from 'react'
import { useState, useEffect } from "react";
import Lyric from "./Lyric"

export default function Game() {
  const songsName = ['we%20will%20rock%20you', 'frozen', 'Beat%20It','Smells%20Like%20Teen%20Spirit','Billie%20Jean']
  const artistsArr =['queen', 'madonna','Michael%20Jackson','Nirvana','Michael%20Jackson']
  let random = Math.floor(songsName.length*Math.random());
  const [randomNum, setRandomNum] = useState(random);
  const [inputValue, setInputValue] = useState('')
  const [score, setScore] = useState(0);
  
  useEffect( () => {
    randomChoice();
  },[inputValue])
  
  const randomChoice = () => {
    // random = Math.floor(songsName.length*Math.random());
    setRandomNum(random)
    // console.log(`randomChoice: ${random}`);
    console.log(`randomChoice: ${randomNum}`);
  }
  const handelClick = () => {
    window.history.back();
    console.log(window.history.back);
  }
  const handelInputChange = (val) => {
    randomChoice();
    setInputValue(val)
    if (inputValue == songsName[0].split('%20').join(' ')) {
      console.log('correct');
      setScore(score+5)
    }
  }
  const createLyrics =() => {
    return <Lyric songName = {songsName[randomNum]} artistName = {artistsArr[randomNum]}/>

  }

  return (
    <div className = "game-container">
    <input placeholder="Guess The Song Name" type="text" value = {inputValue} onChange={event => handelInputChange(event.target.value)}/>
    <button className="score">score: {score}</button>
    <div>{randomNum}</div>
    {createLyrics()}
    <button onClick={handelClick}>Back</button>
    {console.log(inputValue)}
    </div>
  )
}
