  const artistName =['queen', 'madonna','Michael%20Jackson','Nirvana','Michael%20Jackson']
  const [songChoice, setChoice] = useState(null);


  const randomChoice = () => {
    let random = Math.floor(songsName.length*Math.random());
    setChoice(random)
    console.log(random);
  }


  return (
    <div>
